mapper.select:
select
id, weather_year, avg_value ,userid
from busdata.weather_data


        and id = #{id}


        and weather_year = #{weatherYear}


        and avg_value = #{avgValue}


        and userid = #{userid}

mapper.update:
update busdata.weather_data


        weather_year = #{weatherYear},


        avg_value = #{avgValue},


        userid = #{userid}


where id = #{id}
